(function(){"use strict";const e=document.querySelector("body"),t=document.querySelector(".mobile-nav__sheet"),n=document.querySelector(".mobile-nav__toggle");function o(){e.classList.add("js-nav-open"),t.setAttribute("aria-hidden","false"),n.setAttribute("aria-expanded","true")}function s(){e.classList.remove("js-nav-open"),t.setAttribute("aria-hidden","true"),n.setAttribute("aria-expanded","false")}const i=document.querySelectorAll(".mobile-nav__cover, .mobile-nav__toggle");i.forEach(function(t){t.addEventListener("click",function(t){t.preventDefault(),e.classList.contains("js-nav-open")?s():o()})});const a=document.querySelectorAll(".mobile-nav__sheet a");a.forEach(function(e){e.addEventListener("click",function(){s()})})})()